<?php $liaProject = new liaProject();$course = new Course();$CompanyObj = new Company();$UserObj = new User();if(isset($_GET['deleteid'])){	$liaProject->removeApplicant($_GET['deleteid']);	redirect(CURRENT_PATH);}if(isset($_GET['accept']) && isset($_GET['uid'])){	$userEmail = $UserObj->getUserByid($_GET['uid'])['email'];	$subject = 'LIA-plats accepterad';	$msg = 'Din sökta LIA-plats är accepterad, logga in på LIAbanken för mer information.';	mail($userEmail, $subject, $msg, 'From: noreply@liabanken.se');	$liaProject->acceptApplicant($_GET['uid'], $_GET['accept']);	redirect(CURRENT_PATH);}if(isset($_GET['deny']) && isset($_GET['uid'])){	$userEmail = $UserObj->getUserByid($_GET['uid'])['email'];	$subject = 'LIA-plats nekad';	$msg = 'Din sökta LIA-plats är nekad, logga in på LIAbanken för mer information.';	mail($userEmail, $subject, $msg, 'From: noreply@liabanken.se');	$liaProject->denyApplicant($_GET['uid'], $_GET['deny']);	redirect(CURRENT_PATH);}if(isset($_GET['finish']) && isset($_GET['uid'])){	$liaProject->finishApplicant($_GET['uid'], $_GET['finish']);	redirect(CURRENT_PATH);}if($signedUser['student']){	$myApplications = $liaProject->getMyApplicationsUser($signedUser['id']);	$thName = 'Företag';}elseif($signedUser['company_owner']){	$myApplications = $liaProject->getMyApplicationsCompany($signedUser['company_id']);	$thName = "Elevens namn";}if(isset($_POST['report']) && isset($_GET['app']) && isset($_GET['id'])){	$report = $_POST['report'];	$applicant = $_GET['app'];	$id = $_GET['id'];	if(strlen($report) > 200){		//error här, lämnar tomt så att jocke kan fixa sen. :) Ha så kul Joeck!!1	}	else{		$liaProject->editReportApplicant($applicant, $id, $report);	}		redirect(CURRENT_PATH);}?><div class="container">    <div class="jumbotron">        <h1>Ansöknings info</h1>    </div></div><div class="container">		<?php if(count($myApplications) == 0){			echo '<h3 class="text-center">Inga ansökningar</h3>';			}else{ ?>			<table class="table table-hover">				<thead>					<tr>						<th><?php echo $thName; ?></th>						<th>Lia kurs</th>						<th>Status</th>						<th>Ansökning slutar</th>						<th></th>					</tr>				</thead>				<tbody>					<tr> 					 	<td><a href="<?php echo $path.'student-profile?uid='.$user['guid']; ?>"><?php echo $tdName; ?></a></td>					 	<td><?php echo $courseName; ?></td>					 	<td><b><?php echo $status; ?></b></td>					 	<td><?php echo $lastDay; ?></td>					 	<td><?php echo $btn.$deleteBtn; ?></td>					 </tr>				 	 <?php } ?>			</tbody>		</table>	</div></body></html>